#Treinando o ChatBot nos conteudos
chatbot.train(conversa_Humana)
chatbot.train(conversa_Negocio)

#Iniciando Loop
chatativo = True
while chatativo == True:
    pergunta = input("Eu: ")
    if pergunta.lower() == "sair":
        chatativo = False
    else:
        resposta = chatbot.get_response(pergunta)
        print("Bot:", resposta)
print ("Fim do Chatbot")